export default class Provider {
  setClient (client) {
    this.client = client
  }

  getMethod (method) {
    const provider = this.client.getProviderForMethod(method, this)
    return provider[method].bind(provider)
  }
}

import { sha256, ripemd160 } from 'bcrypto'
import base58 from 'bs58'
import bech32 from 'bech32'

const crypto = {
  /**
   * Base58 object with decode, decodeUnsafe, and encode functions.
   */
  base58,

  /**
   * Get bech32 of message.
   * @param {!string} message - any string.
   * @return {string} Returns the bech32 of a string.
   */
  bech32,

  /**
   * Get hash160 of message.
   * @param {!string} message - any string.
   * @return {string} Returns the hash160 of a string.
   */
  hash160 (message) {
    const sha256Hashed = this.sha256(message)
    const ripemd160Hashed = this.ripemd160(sha256Hashed)
    return ripemd160Hashed
  },

  /**
   * Get sha256 of message.
   * @param {!string} message - any string.
   * @return {string} Returns the sha256 of a string.
   */
  sha256,

  /**
   * Get ripemd160 of message.
   * @param {!string} message - any string.
   * @return {string} Returns the ripemd160 of a string.
   */
  ripemd160,

  /**
   * Ensure message is in buffer format.
   * @param {string} message - any string.
   * @return {string} Returns Buffer of string.
   */
  ensureBuffer (message) {
    if (typeof message === 'string') {
      message = Buffer.from(message, 'hex')
    }
    return message
  }
}

export default crypto

import 'regenerator-runtime/runtime'
import { find, findLast, findLastIndex, isArray, isBoolean, isFunction, isNumber, isString } from 'lodash'
import * as Ajv from 'ajv'

import Provider from './Provider'
import providers from './providers'
import crypto from './crypto'
import BlockSchema from './schema/Block.json'
import TransactionSchema from './schema/Transaction.json'

export default class ChainAbstractionLayer {
  /**
   * ChainAbstractionLayer client
   * @param {object} [provider] - The provider instance.
   * @param {string} [version] - Version string
   */
  constructor (provider, version) {
    this.version = version
    this._providers = []

    const ajv = new Ajv()
    this.validateTransaction = ajv.compile(TransactionSchema)
    this.validateBlock = ajv.compile(BlockSchema)

    if (provider) {
      this.addProvider(provider)
    }
  }

  /**
   * Add a provider.
   * @param {Object} provider - The provider instance.
   * @return {Client}
   */
  addProvider (provider) {
    if (!isFunction(provider.setClient)) {
      throw new Error(`Invalid provider`)
    }

    const duplicate = find(this._providers, _provider => provider.constructor === _provider.constructor)

    if (duplicate) {
      throw new Error('Duplicate provider')
    }

    provider.setClient(this)
    this._providers.push(provider)
    return this
  }

  /**
   * Check the availability of a method.
   * @return {boolean}
   */
  getProviderForMethod (method, requestor = false) {
    if (this._providers.length === 0) {
      throw new Error('No provider provided')
    }

    const indexOfRequestor = requestor
      ? findLastIndex(this._providers, provider => requestor.constructor === provider.constructor)
      : this._providers.length

    const provider = findLast(this._providers, provider => isFunction(provider[method]), indexOfRequestor - 1)

    if (provider == null) {
      throw new Error(`Unimplemented method: ${method}`)
    }

    if (isFunction(provider._checkMethodVersionSupport)) {
      if (!provider._checkMethodVersionSupport(method, this.version)) {
        throw new Error(`Method "${method}" is not supported by version "${this.version}"`)
      }
    }

    return provider
  }

  /**
   * Generate a block
   * @param {!number} numberOfBlocks - Number of blocks to be generated
   * @return {Promise<string[], Error>} Returns a promise with Block hash of the
   *  generated blocks if resolved. Throws an Error if rejected.
   */
  async generateBlock (numberOfBlocks) {
    const provider = this.getProviderForMethod('generateBlock')

    if (!isNumber(numberOfBlocks)) {
      throw new Error('Invalid number of blocks to be generated')
    }

    const blockHashes = await provider.generateBlock(numberOfBlocks)

    if (!isArray(blockHashes)) {
      throw new Error('Provider returned an invalid response')
    }

    const invalidBlock = find(blockHashes, blockHash => !(/^[A-Fa-f0-9]+$/.test(blockHash)))

    if (invalidBlock) {
      throw new Error('Provider returned an invalid response')
    }

    return blockHashes
  }

  /**
   * Get a block given its hash.
   * @param {!string} blockHash - A hexadecimal string that represents the *hash* of the
   *  desired block.
   * @param {boolean} [includeTx=false] - If true, fetches transaction in the block.
   * @return {Promise<Client.schemas.Block, Error>} Returns a Block with the same hash as
   *  the given input. Throws an Error if no block was found. If `includeTx` is true, the
   *  transaction property is an array of Transactions; otherwise, it is a list of
   *  transaction hashes.
   */
  async getBlockByHash (blockHash, includeTx = false) {
    const provider = this.getProviderForMethod('getBlockByHash')

    if (!isString(blockHash)) {
      throw new Error('Block hash should be a string')
    }

    if (!(/^[A-Fa-f0-9]+$/.test(blockHash))) {
      throw new Error('Block hash should be a valid hex string')
    }

    if (!isBoolean(includeTx)) {
      throw new Error('Second parameter should be boolean')
    }

    const block = await provider.getBlockByHash(blockHash, includeTx)

    if (!this.validateBlock(block)) {
      throw new Error('Provider returned an invalid block')
    }

    return block
  }

  /**
   * Get a block given its number.
   * @param {!number} blockNumber - The number of the desired block.
   * @param {boolean} [includeTx=false] - If true, fetches transaction in the block.
   * @return {Promise<Client.schemas.Block, Error>} Returns a Block with the same number as
   *  the given input. Throws an Error if no block was found. If `includeTx` is true, the
   *  transaction property is an array of Transactions; otherwise, it is a list of
   *  transaction hashes.
   */
  async getBlockByNumber (blockNumber, includeTx = false) {
    const provider = this.getProviderForMethod('getBlockByNumber')

    if (!isNumber(blockNumber)) {
      throw new Error('Invalid Block number')
    }

    if (!isBoolean(includeTx)) {
      throw new Error('Second parameter should be boolean')
    }

    const block = await provider.getBlockByNumber(blockNumber, includeTx)

    const valid = this.validateBlock(block)

    if (!valid) {
      const errors = this.validateBlock.errors
      throw new Error(`Provider returned an invalid block, ${errors[0].dataPath} ${errors[0].message}`)
    }

    return block
  }

  /**
   * Get the current block height of the chain.
   * @return {Promise<number, Error>}
   */
  async getBlockHeight () {
    const provider = this.getProviderForMethod('getBlockHeight')

    const blockHeight = await provider.getBlockHeight()

    if (!isNumber(blockHeight)) {
      throw new Error('Provider returned an invalid block height')
    }

    return blockHeight
  }

  /**
   * Get a transaction given its hash.
   * @param {!string} txHash - A hexadecimal string that represents the *hash* of the
   *  desired transaction.
   * @return {Promise<Client.schemas.Transaction, Error>} Returns a Transaction with the
   *  same hash as the given input. Throws an Error if no transaction was found.
   */
  async getTransactionByHash (txHash) {
    const provider = this.getProviderForMethod('getTransactionByHash')

    if (!isString(txHash)) {
      throw new Error('Transaction hash should be a string')
    }

    if (!(/^[A-Fa-f0-9]+$/.test(txHash))) {
      throw new Error('Transaction hash should be a valid hex string')
    }

    const transaction = await provider.getTransactionByHash(txHash)

    const valid = this.validateTransaction(transaction)

    if (!valid) {
      const errors = this.validateTransaction.errors
      throw new Error(`Provider returned an invalid transaction, ${errors[0].dataPath} ${errors[0].message}`)
    }

    return transaction
  }

  /**
   * Get a raw hexadecimal transaction given its hash.
   * @param {!string} txHash - A hexadecimal string that represents the *hash* of the
   *  desired transaction.
   * @return {Promise<string, Error>} Returns the raw Transaction with the same hash as the
   *  given output. Throws an Error if no transaction was found.
   */
  async getRawTransactionByHash (txHash) {
    const provider = this.getProviderForMethod('getRawTransactionByHash')

    if (!isString(txHash)) {
      throw new Error('Transaction hash should be a string')
    }

    if (!(/^[A-Fa-f0-9]+$/.test(txHash))) {
      throw new Error('Transaction hash should be a valid hex string')
    }

    const transaction = await provider.getRawTransactionByHash(txHash)

    if (!this.validateTransaction(transaction)) {
      throw new Error('Provider returned an invalid transaction')
    }

    return transaction
  }

  async getAddresses () {
    const provider = this.getProviderForMethod('getAddresses')

    const addresses = await provider.getAddresses()

    if (!isArray(addresses)) {
      throw new Error('Provider returned an invalid response')
    }

    return addresses
  }

  async signMessage (message, from) {
    const provider = this.getProviderForMethod('signMessage')

    const signedMessage = await provider.signMessage(message, from)

    return signedMessage
  }

  async sendTransaction (from, to, value, data) {
    const provider = this.getProviderForMethod('sendTransaction')

    const txHash = await provider.sendTransaction(from, to, value, data)

    if (!isString(txHash)) {
      throw new Error('sendTransaction method should return a transaction id string')
    }

    return txHash
  }

  async generateSecret (message) {
    const addresses = await this.getAddresses()
    const from = addresses[0]
    const signedMessage = await this.signMessage(message, from)
    const secret = crypto.hash160(signedMessage)
    return secret
  }
}

ChainAbstractionLayer.Provider = Provider
ChainAbstractionLayer.providers = providers
ChainAbstractionLayer.schemas = {
  Block: BlockSchema,
  Transaction: TransactionSchema
}
ChainAbstractionLayer.crypto = crypto

import axios from 'axios'
import JSONBigInt from 'json-bigint'
import { get, has } from 'lodash'

import Provider from '../Provider'
import RpcError from './RpcError'

const { parse } = JSONBigInt({ storeAsString: true, strict: true })

export default class JsonRpcProvider extends Provider {
  constructor (uri, username, password) {
    super()

    this._axios = axios.create({
      baseURL: uri,
      responseType: 'text',
      transformResponse: undefined // https://github.com/axios/axios/issues/907
    })

    if (username || password) {
      this._axios.defaults.auth = { username, password }
    }
  }

  _prepareRequest (method, params) {
    const id = Date.now()
    return { id, method, params }
  }

  _parseResponse ({ data, status, statusText, headers }) {
    if (typeof data === 'string' && headers['content-type'] !== 'application/json' && status !== 200) {
      throw new RpcError(status, statusText, { data })
    }

    data = parse(data)

    if (data.error != null) {
      throw new RpcError(
        get(data, 'error.code', -32603),
        get(data, 'error.message', 'An error occurred while processing the RPC call')
      )
    }

    if (!has(data, 'result')) {
      throw new RpcError(-32700, 'Missing `result` on the RPC call result')
    }

    return data.result
  }

  rpc (method, ...params) {
    return this._axios.post(
      '/',
      this._prepareRequest(method, params)
    ).then(this._parseResponse).catch(e => console.error(e))
  }
}

import { STATUS_CODES } from 'http'
import StandardError from './StandardError'

export default class RpcError extends StandardError {
  constructor (code, msg, props = {}) {
    if (typeof code !== 'number') {
      throw new TypeError(`Non-numeric HTTP code`)
    }

    if (typeof msg === 'object' && msg !== null) {
      props = msg
      msg = null
    }

    props.code = code

    super(msg || STATUS_CODES[code], props)
  }

  get status () {
    return this.code
  }

  set status (value) {
    Object.defineProperty(this, 'status', {
      configurable: true,
      enumerable: true,
      value,
      writable: true
    })
  }

  toString () {
    return `${this.name}: ${this.code} ${this.message}`
  }
}

import crypto from '../../crypto'

const networks = {
  mainnet: '00',
  testnet: '6F',
  litecoin: '30'
}

const BitcoinCrypto = {
  /**
   * Get compressed pubKey from pubKey.
   * @param {!string} pubkey - 65 byte string with prefix, x, y.
   * @return {string} Returns the compressed pubKey of uncompressed pubKey.
   */
  compressPubKey (pubKey) {
    let x = pubKey.substring(2, 66)
    let y = pubKey.substring(66, 130)
    let prefix
    let even = parseInt(y.substring(62, 64), 16) % 2 === 0
    even ? prefix = '02' : prefix = '03'
    return prefix + x
  },

  /**
   * Get address from pubKey.
   * @param {!string} pubkey - 65 byte uncompressed pubKey or 33 byte compressed pubKey.
   * @return {string} Returns the address of pubKey.
   */
  pubKeyToAddress (pubKey, network) {
    pubKey = crypto.ensureBuffer(pubKey)
    const pubKeyHash = crypto.hash160(pubKey)
    const addr = this.pubKeyHashToAddress(pubKeyHash, network)
    return addr
  },

  /**
   * Get address from pubKeyHash.
   * @param {!string} pubKeyHash - hash160 of pubKey.
   * @return {string} Returns the address derived from pubKeyHash.
   */
  pubKeyHashToAddress (pubKeyHash, network) {
    pubKeyHash = crypto.ensureBuffer(pubKeyHash)
    const prefixHash = Buffer.concat([Buffer.from(networks[network], 'hex'), pubKeyHash])
    const checksum = crypto.sha256(crypto.sha256(prefixHash)).slice(0, 4)
    const addr = crypto.base58.encode(Buffer.concat([prefixHash, checksum]))
    return addr
  },

  /**
   * Get pubKeyHash from address.
   * @param {!string} address - bitcoin base58 encoded address.
   * @return {string} Returns the pubKeyHash of bitcoin address.
   */
  addressToPubKeyHash (address) {
    return crypto.base58.decode(address).toString('hex').substring(2, 42)
  }
}

export default BitcoinCrypto

import Provider from '../../Provider'

import Transport from '@alias/ledger-transport'
import LedgerBtc from '@ledgerhq/hw-app-btc'

export default class BitcoinLedgerProvider extends Provider {
  constructor () {
    super()
    this._ledgerBtc = false
    this._derivationPath = `44'/0'/0'/0`
  }

  async _connectToLedger () {
    if (!this._ledgerBtc) {
      const transport = await Transport.create()
      this._ledgerBtc = new LedgerBtc(transport)
    }
  }

  async _updateDerivationPath (path) {
    this._derivationPath = path
  }

  async getAddresses () {
    await this._connectToLedger()

    const { bitcoinAddress } = await this._ledgerBtc.getWalletPublicKey(this._derivationPath)

    return [ bitcoinAddress ]
  }

  async signMessage (message, from) {
    await this._connectToLedger()

    const hex = Buffer.from(message).toString('hex')

    return this._ledgerBtc.signMessageNew(this._derivationPath, hex)
  }
}

import JsonRpcProvider from '../JsonRpcProvider'

export default class BitcoinRPCProvider extends JsonRpcProvider {
  async _decodeRawTransaction (rawTransaction) {
    const data = await this.rpc('decoderawtransaction', rawTransaction)
    const { hash: txHash, txid: hash, vout } = data
    const value = vout.reduce((p, n) => p + parseInt(n.value), 0)

    const output = { hash, value, _raw: { hex: rawTransaction, data, txHash } }

    return output
  }

  async generateBlock (numberOfBlocks) {
    return this.rpc('generate', numberOfBlocks)
  }

  async getBlockByHash (blockHash, includeTx) {
    const data = await this.rpc('getblock', blockHash)
    const { hash,
      height: number,
      time: timestamp,
      difficulty,
      size,
      previousblockhash: parentHash,
      nonce,
      confirmations } = data
    let { tx: transactions } = data

    if (includeTx) {
      const txs = transactions.map(this.getTransactionByHash)
      transactions = await Promise.all(txs)
    }

    return { hash,
      number,
      timestamp,
      difficulty,
      size,
      parentHash,
      nonce,
      transactions,
      confirmations }
  }

  async getBlockByNumber (blockNumber, includeTx) {
    return this.getBlockByHash(await this.rpc('getblockhash', blockNumber), includeTx)
  }

  async getTransactionByHash (transactionHash) {
    const rawTx = await this.getRawTransactionByHash(transactionHash)
    const tx = await this._decodeRawTransaction(rawTx)
    const data = await this.rpc('gettransaction', transactionHash)

    const { confirmations } = data
    const output = Object.assign({}, tx, { confirmations })

    if (confirmations > 0) {
      const { blockhash: blockHash } = data
      const { number: blockNumber } = await this.getBlockByHash(blockHash)
      Object.assign(output, { blockHash, blockNumber })
    }

    return output
  }

  async getRawTransactionByHash (transactionHash) {
    return this.rpc('getrawtransaction', transactionHash)
  }
}

