import Provider from '../../Provider'

import { isFunction } from 'lodash'
import { formatEthResponse, ensureEthFormat } from './EthereumUtil'

export default class EthereumMetaMaskProvider extends Provider {
  constructor (metamaskProvider) {
    super()
    if (!isFunction(metamaskProvider.sendAsync)) {
      throw new Error('Invalid MetaMask Provider')
    }

    this._metamaskProvider = metamaskProvider
  }

  _toMM (method, ...params) {
    return new Promise((resolve, reject) => {
      this
        ._metamaskProvider
        .sendAsync({ method, params }, (err, data) => {
          if (err) {
            reject(err)
            return
          }

          if (!data) {
            reject(new Error('Something went wrong'))
            return
          }

          if (!data.result) {
            reject(new Error('Something went wrong'))
            return
          }

          const formattedResult = formatEthResponse(data.result)

          resolve(formattedResult)
        })
    })
  }

  async getAddresses () {
    return this._toMM('eth_accounts')
  }

  async signMessage (message, from) {
    const hex = Buffer.from(message).toString('hex')

    return this._toMM('personal_sign', `0x${hex}`, from)
  }

  async sendTransaction (from, to, value, data) {
    const tx = {
      from, to, value, data
    }

    return this._toMM('eth_sendTransaction', tx)
  }

  async getBlockByNumber (blockNumber, includeTx) {
    return this._toMM('eth_getBlockByNumber', '0x' + blockNumber.toString(16), includeTx)
  }

  async getTransactionByHash (txHash) {
    txHash = ensureEthFormat(txHash)
    return this._toMM('eth_getTransactionByHash', txHash)
  }
}

import JsonRpcProvider from '../JsonRpcProvider'

import { formatEthResponse, ensureEthFormat } from './EthereumUtil'

export default class EthereumRPCProvider extends JsonRpcProvider {
  _parseResponse (response) {
    const data = super._parseResponse(response)

    return formatEthResponse(data)
  }

  async getAddresses () {
    return this.rpc('eth_accounts')
  }

  async generateBlock (numberOfBlocks) {
    // Q: throw or silently pass?
    throw new Error('This method isn\'t supported by Ethereum')
  }

  async getBlockByNumber (blockNumber, includeTx) {
    return this._rpc('eth_getBlockByNumber', blockNumber, includeTx)
  }

  async getTransactionByHash (txHash) {
    txHash = ensureEthFormat(txHash)
    return this._rpc('eth_getTransactionByHash', txHash)
  }
}

import BlockSchema from '../../schema/Block.json'
import TransactionSchema from '../../schema/Transaction.json'

export function formatEthResponse (obj) {
  if (Array.isArray(obj)) {
    obj = obj.map((elem) => {
      if (elem.startsWith('0x')) {
        elem = elem.replace('0x', '')
      }
      return elem
    })
  } else {
    for (let key in obj) {
      if (Array.isArray(obj[key])) {
        obj[key] = formatEthResponse(obj[key])
      } else {
        if ((BlockSchema.properties[key] &&
          BlockSchema.properties[key].type === 'number') ||
          (TransactionSchema.properties[key] &&
          TransactionSchema.properties[key].type === 'number')) {
          obj[key] = parseInt(obj[key])
        } else {
          if (obj[key].startsWith('0x')) {
            obj[key] = obj[key].replace('0x', '')
          }
        }
      }
    }
  }
  return obj
}

export function ensureEthFormat (hash) {
  if (!hash.startsWith('0x')) { hash = '0x' + hash }
  return hash
}

